    public Mono<City> mockFetchNews(City city) {
        String news = recentNews.get(city.getName());
        City newCity;
        if (news == null) {
            newCity = new City();
        } else {
            try {
                newCity = objectMapper.readValue(news, City.class);
            } catch (JsonProcessingException e) {
                throw new RuntimeException(e);
            }
        }
        newCity.setName(city.getName());
        newCity.setState(city.getState());
        newCity.setUpdateDateToNow();
        return Mono.just(newCity);
    }

