    public Mono<City> mockFetchCity(String query) {
        String lowerCase = query.split(",")[0].toLowerCase();
        Optional<String> optionalCity = mockCities.entrySet().stream()
                .filter(entry -> entry.getKey().startsWith(lowerCase))
                .map(Map.Entry::getValue)
                .findFirst();
        if (optionalCity.isEmpty()) {
            return Mono.error(new RuntimeException("404"));
        }
        String mockCity = optionalCity.get();

        ObjectMapper objectMapper = new ObjectMapper();
        City city;
        try {
            city = objectMapper.readValue(mockCity, City.class);
        } catch (JsonProcessingException e) {
            throw new RuntimeException(e);
        }
        return Mono.just(city);
    }
