    public Mono<City> mockFetchNews(City city, String newsStatus) {
        Map<String, String> newsMap;

        if (newsStatus.equals("recent")) {
            newsMap = recentNews;
        } else if (newsStatus.equals("old")) {
            newsMap = oldNews;
        } else {
            return null;
        }

        String news = newsMap.get(city.getName());
        City newCity;
        if (news == null) {
            newCity = new City();
        } else {
            try {
                newCity = objectMapper.readValue(news, City.class);
            } catch (JsonProcessingException e) {
                throw new RuntimeException(e);
            }
        }
        return Mono.just(newCity)
                .doOnNext(City::setUpdateDateToNow);
    }


