    public HttpRequest prepareRequest(String host, String path, Map<String, String> params,
                                      Map<String, String> headers) {
        String query = "";
        if (params != null) {
            query = params.entrySet().stream()
                    .map(e -> String.format("%s=%s", e.getKey(), e.getValue()))
                    .collect(Collectors.joining("&"));
        }

        URI uri;
        try {
            uri = new URI("https", null, host, -1, path, query, null);
        } catch (URISyntaxException e) {
            throw new IllegalArgumentException(e.getMessage(), e);
        }

        HttpRequest.Builder httpRequestBuilder = HttpRequest.newBuilder().uri(uri);
        if (headers != null) {
            headers.forEach(httpRequestBuilder::header);
        }
        return httpRequestBuilder.build();
    }

    public Mono<City> prepareResponse(String host, String path, Map<String, String> params,
                                      Map<String, String> headers) {
        HttpRequest request = prepareRequest(host, path, params, headers);

//        String statesPath = "/home/zalman/Documents/JavaProjects/NewsRequests/";
//        CompletableFuture<HttpResponse<String>> response = httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString());
//        CompletableFuture<String> stringCompletableFuture = response.thenApply(httpResponse -> {
//            String body = httpResponse.body();
//            try {
//                Files.writeString(Paths.get(statesPath, "headers.txt"), httpResponse.headers().toString().replaceAll("],", "].\n"));
//                Files.writeString(Paths.get(statesPath, "news.json"), body);
//            } catch (IOException e) {
//                throw new RuntimeException(e);
//            }
//            return body;
//        });

        return Mono.fromFuture(httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString()))
                .map(HttpResponse::body).flatMap(body -> {
                    City city;
                    try {
                        city = objectMapper.readValue(body, City.class);
                    } catch (JsonProcessingException ex) {
                        return Mono.error(new RuntimeException(ex));
                    }
                    return Mono.just(city);
                });
    }

    public Mono<City> fetchNews(City city) {
        String query = city.getName().equals(TRENDING) ? "usa" : city.prepareQuery();
        LinkedHashMap<String, String> params = new LinkedHashMap<>(Map.of("q", query));
        params.putAll(NEWS_API_URI_PARAMS);

        return prepareResponse(NEWS_HOST, NEWS_PATH, params, NEWS_API_URI_HEADERS)
                .map(fetchedCity -> {
                    fetchedCity.setName(city.getName());
                    fetchedCity.setState(city.getState());
                    fetchedCity.setUpdateDateToNow();
                    return fetchedCity;
                });
    }

